function formatDateTime(date) {
    const pad = (num) => num.toString().padStart(2, '0');

    const day = pad(date.getDate());
    const month = pad(date.getMonth() + 1);
    const year = date.getFullYear();

    const hours = pad(date.getHours());
    const minutes = pad(date.getMinutes());

    return `${day}.${month}.${year} / ${hours}:${minutes} /`;
}

function updateDateTime() {
    const now = new Date();
    document.getElementById('datetime').textContent = formatDateTime(now) + ' Россия, ';
}

updateDateTime(); // первый вызов сразу

// Рассчитаем время до следующей минуты, чтобы обновлять ровно каждую минуту
const now = new Date();
const delay = (60 - now.getSeconds()) * 1000;

setTimeout(() => {
    updateDateTime();
    setInterval(updateDateTime, 60000);
}, delay);
let width;
window.addEventListener('resize', () => {
    width = window.innerWidth;
    adaptGeolocation();
})
document.addEventListener('DOMContentLoaded', () => {
    width = window.innerWidth;
    adaptGeolocation();
});

function adaptGeolocation() {
    if (width <= 780) {
        document.querySelector('.header__body-geolocation').remove();
    } else {
        document.querySelector('.main__geolocation').remove();
    }
}